import React, { useEffect, useState } from 'react';

const QuizWord = () => {
  const [quiz, setQuiz] = useState(null);
  const [selected, setSelected] = useState('');
  const [result, setResult] = useState('');

  const fetchQuiz = async () => {
    setResult('');
    setSelected('');
    const res = await fetch('http://localhost:5000/api/quiz/word');
    const data = await res.json();
    setQuiz(data);
  };

  useEffect(() => {
    fetchQuiz();
  }, []);

  const handleChoice = (choice) => {
    setSelected(choice);
    if (choice === quiz.correct) {
      setResult('‚úÖ Ï†ïÎãµÏûÖÎãàÎã§!');
    } else {
      setResult(`‚ùå Ïò§ÎãµÏûÖÎãàÎã§! Ï†ïÎãµ: ${quiz.correct}`);
    }
  };

  if (!quiz) return <p>ÌÄ¥Ï¶à Î°úÎî© Ï§ë...</p>;

  return (
    <div style={{
      padding: '2rem',
      maxWidth: '500px',
      margin: 'auto',
      border: '1px solid #ccc',
      borderRadius: '10px',
      boxShadow: '0 0 10px rgba(0,0,0,0.1)'
    }}>
      <h2 style={{ textAlign: 'center' }}>üß† Îúª Í≥†Î•¥Í∏∞ ÌÄ¥Ï¶à</h2>

      <div style={{
        background: '#f7f7f7',
        padding: '1rem',
        borderRadius: '8px',
        marginBottom: '1rem',
        textAlign: 'center'
      }}>
        <h3 style={{ margin: 0 }}>{quiz.english}</h3>
      </div>

      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        {quiz.choices.map((choice, idx) => (
          <button
            key={idx}
            style={{
              padding: '0.7rem',
              borderRadius: '5px',
              border: selected ? (choice === quiz.correct ? '2px solid green' : (choice === selected ? '2px solid red' : '1px solid #ccc')) : '1px solid #ccc',
              backgroundColor: selected ? (choice === quiz.correct ? '#d4edda' : (choice === selected ? '#f8d7da' : '#fff')) : '#fff',
              cursor: selected ? 'default' : 'pointer',
              transition: '0.2s'
            }}
            disabled={!!result}
            onClick={() => handleChoice(choice)}
          >
            {choice}
          </button>
        ))}
      </div>

      <div style={{ marginTop: '1rem', textAlign: 'center' }}>
        <p>{result}</p>
        {result && <button onClick={fetchQuiz}>Îã§Ïùå Î¨∏Ï†ú ‚ñ∂Ô∏è</button>}
      </div>
    </div>
  );
};

export default QuizWord;
