import React, { useState } from 'react';

const Register = () => {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, username, password }),
      });

      const data = await res.json();

      if (res.ok) {
        setMessage('‚úÖ ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ! Ïù¥Ï†ú Î°úÍ∑∏Ïù∏ÌïòÏÑ∏Ïöî!');
        // üëâ Î∞îÎ°ú Î°úÍ∑∏Ïù∏ÏãúÌÇ§Í≥† Ïã∂Îã§Î©¥ data.token Ï†ÄÏû•ÎèÑ Í∞ÄÎä•
      } else {
        setMessage(`‚ùå ${data.message}`);
      }
    } catch (err) {
      console.error(err);
      setMessage('‚ùå ÌöåÏõêÍ∞ÄÏûÖ Ï§ë Ïò§Î•ò Î∞úÏÉù');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>üìù ÌöåÏõêÍ∞ÄÏûÖ</h2>
      <form onSubmit={handleRegister}>
        <input
          type="email"
          placeholder="Ïù¥Î©îÏùº"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br /><br />
        <input
          type="text"
          placeholder="ÏÇ¨Ïö©Ïûê Ïù¥Î¶Ñ"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        /><br /><br />
        <input
          type="password"
          placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br /><br />
        <button type="submit">ÌöåÏõêÍ∞ÄÏûÖ</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default Register;
