// src/App.js

import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Register from './components/Register';
import WordList from './components/WordList';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true); // true: Î°úÍ∑∏Ïù∏ ÌôîÎ©¥, false: ÌöåÏõêÍ∞ÄÏûÖ
  const [user, setUser] = useState(null);

  // Î°úÍ∑∏Ïù∏ Ïú†ÏßÄ
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setIsLoggedIn(true);
      setUser(JSON.parse(userData));
    }
  }, []);

  const handleLoginSuccess = (userData, token) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    setUser(userData);
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsLoggedIn(false);
  };

  return (
    <div className="App" style={{ padding: '2rem' }}>
      <h1>üß† TOEIC Îã®Ïñ¥ ÌïôÏäµ</h1>

      {isLoggedIn ? (
        <>
          <p>üëã {user?.username}Îãò, ÌôòÏòÅÌï©ÎãàÎã§!</p>
          <button onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
          <WordList />
        </>
      ) : (
        <>
          {showLogin ? (
            <>
              <Login onLogin={handleLoginSuccess} />
              <p>
                Í≥ÑÏ†ïÏù¥ ÏóÜÏúºÏã†Í∞ÄÏöî?{' '}
                <button onClick={() => setShowLogin(false)}>ÌöåÏõêÍ∞ÄÏûÖ</button>
