            <>
              <Register />
              <p>
                Ïù¥ÎØ∏ Í≥ÑÏ†ïÏù¥ ÏûàÏúºÏã†Í∞ÄÏöî?{' '}
                <button onClick={() => setShowLogin(true)}>Î°úÍ∑∏Ïù∏</button>
              </p>
            </>
          )}
        </>
      )}
    </div>
  );
}

export default App;
