import React, { useState } from 'react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (res.ok) {
        setMessage('‚úÖ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
        localStorage.setItem('token', data.token); // ÌÜ†ÌÅ∞ Ï†ÄÏû•
        // üëâ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ Ïãú ÏõêÌïòÎäî ÎèôÏûë Ï∂îÍ∞Ä (Ïòà: Ïù¥Îèô, ÏÉÅÌÉú Î≥ÄÍ≤Ω Îì±)
      } else {
        setMessage(`‚ùå ${data.message}`);
      }
    } catch (err) {
      console.error(err);
      setMessage('‚ùå Î°úÍ∑∏Ïù∏ Ï§ë Ïò§Î•ò Î∞úÏÉù');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>üîê Î°úÍ∑∏Ïù∏</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Ïù¥Î©îÏùº"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br /><br />
        <input
          type="password"
          placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br /><br />
        <button type="submit">Î°úÍ∑∏Ïù∏</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default Login;
