import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const TOTAL_QUESTIONS = 10;

const QuizWord = () => {
  const [quiz, setQuiz] = useState(null);
  const [selected, setSelected] = useState('');
  const [result, setResult] = useState('');
  const [count, setCount] = useState(1);
  const [score, setScore] = useState(0);
  const navigate = useNavigate();

  const fetchQuiz = async () => {
    setResult('');
    setSelected('');
    const res = await fetch('http://localhost:5000/api/quiz/word');
    const data = await res.json();
    setQuiz(data);
  };

  useEffect(() => {
    fetchQuiz();
  }, []);

  const handleChoice = (choice) => {
    setSelected(choice);
    if (choice === quiz.correct) {
      setResult('‚úÖ Ï†ïÎãµÏûÖÎãàÎã§!');
      setScore(prev => prev + 1);
    } else {
      setResult(`‚ùå Ïò§ÎãµÏûÖÎãàÎã§! Ï†ïÎãµ: ${quiz.correct}`);
    }
  };

  if (!quiz) return <p>ÌÄ¥Ï¶à Î°úÎî© Ï§ë...</p>;

  if (count > TOTAL_QUESTIONS) {
    // Ï¢ÖÎ£å Ïãú result ÌôîÎ©¥ÏúºÎ°ú Ïù¥Îèô + Ï†êÏàò ÎÑòÍπÄ
    navigate('/quiz-result', { state: { score, total: TOTAL_QUESTIONS } });
    return null;
  }

  return (
    <div style={{ padding: '2rem', maxWidth: '600px', margin: 'auto' }}>
      <h2>üß† ÌÄ¥Ï¶à {count} / {TOTAL_QUESTIONS}</h2>
      <h3>{quiz.english}</h3>
      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        {quiz.choices.map((choice, idx) => (
          <button
            key={idx}
            disabled={!!result}
            onClick={() => handleChoice(choice)}
          >
            {choice}
          </button>
        ))}
      </div>
      <p>{result}</p>

      {/* Îã§Ïùå Î¨∏Ï†ú or ÌÄ¥Ï¶à Ï¢ÖÎ£å */}
      {result && (
        <>
          <button onClick={() => setCount(count + 1)}>Îã§Ïùå Î¨∏Ï†ú ‚ñ∂Ô∏è</button>
          <br /><br />
          <button onClick={() => navigate('/')}>Î©îÏù∏ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
        </>
      )}
    </div>
  );
};

export default QuizWord;
