// src/components/Register.js

import React, { useState } from 'react';

const Register = () => {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, username, password }),
      });

      const data = await res.json();

      if (res.ok) {
        setMessage('‚úÖ ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ! Î°úÍ∑∏Ïù∏ Ìï¥Ï£ºÏÑ∏Ïöî.');
      } else {
        setMessage(`‚ùå ${data.message}`);
      }
    } catch (err) {
      console.error(err);
      setMessage('‚ùå ÌöåÏõêÍ∞ÄÏûÖ Ï§ë Ïò§Î•ò Î∞úÏÉù');
    }
  };

  return (
    <div style={{
      padding: '2rem',
      maxWidth: '400px',
      margin: '3rem auto',
      background: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      textAlign: 'center'
    }}>
      <h2>üìù ÌöåÏõêÍ∞ÄÏûÖ</h2>

      <form onSubmit={handleRegister} style={{ display: 'flex', flexDirection: 'column', gap: '15px', marginTop: '1.5rem' }}>
        <input
          type="email"
          placeholder="Ïù¥Î©îÏùº"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ padding: '0.7rem', borderRadius: '6px', border: '1px solid #ccc' }}
        />

        <input
          type="text"
          placeholder="ÏÇ¨Ïö©Ïûê Ïù¥Î¶Ñ"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          style={{ padding: '0.7rem', borderRadius: '6px', border: '1px solid #ccc' }}
        />

        <input
          type="password"
          placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ padding: '0.7rem', borderRadius: '6px', border: '1px solid #ccc' }}
        />

        <button type="submit" style={{
          padding: '0.8rem',
          borderRadius: '8px',
          border: 'none',
          background: '#339af0',
          color: '#fff',
          cursor: 'pointer'
        }}>
          ÌöåÏõêÍ∞ÄÏûÖ
        </button>
      </form>

      <p style={{ color: '#888', marginTop: '1rem' }}>{message}</p>
    </div>
  );
};

export default Register;
