// src/components/Login.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();

      if (res.ok) {
        setMessage('‚úÖ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
        onLogin(data.user, data.token);
      } else {
        setMessage(`‚ùå ${data.message}`);
      }
    } catch (err) {
      console.error(err);
      setMessage('‚ùå Î°úÍ∑∏Ïù∏ Ï§ë Ïò§Î•ò Î∞úÏÉù');
    }
  };

  return (
    <div style={{
      padding: '2rem',
      maxWidth: '400px',
      margin: '3rem auto',
      background: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      textAlign: 'center'
    }}>
      <h2>üîê Î°úÍ∑∏Ïù∏</h2>

      <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: '15px', marginTop: '1.5rem' }}>
        <input type="email" placeholder="Ïù¥Î©îÏùº" value={email} onChange={(e) => setEmail(e.target.value)} required style={{ padding: '0.7rem', borderRadius: '6px', border: '1px solid #ccc' }} />
        <input type="password" placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏" value={password} onChange={(e) => setPassword(e.target.value)} required style={{ padding: '0.7rem', borderRadius: '6px', border: '1px solid #ccc' }} />
        <button type="submit" style={{ padding: '0.8rem', borderRadius: '8px', border: 'none', background: '#339af0', color: '#fff', cursor: 'pointer' }}>Î°úÍ∑∏Ïù∏</button>
      </form>

      <p style={{ color: '#888', marginTop: '1rem' }}>{message}</p>

      <p style={{ marginTop: '1rem' }}>
        Í≥ÑÏ†ïÏù¥ ÏóÜÏúºÏã†Í∞ÄÏöî?{" "}
        <button type="button" onClick={() => navigate('/register')} style={{ background: 'none', border: 'none', color: '#339af0', cursor: 'pointer', textDecoration: 'underline' }}>
          ÌöåÏõêÍ∞ÄÏûÖ
        </button>
      </p>
    </div>
  );
};

export default Login;
